mapper.select:
select * from ( select rowNum as recNum,t.* 
	from ( select * from TICKET_ORDER where to_char(pay_order_time,'YYYY-MM-DD') 
	between #{beginDate} and #{endDate} 
 AND orderer_id=#{search_word} 
 AND orderer_name=#{search_word} 
 AND pay_orderer_hp_num=#{search_word} 
order by PAY_ORDER_TIME desc ) t ) where recNum between (#{section}-1)*100+ (#{pageNum}-1)*10+1 and (#{section}-1)*100+(#{pageNum})*10

mapper.update:
update TICKET_GOODS
goods_sort=#{goods_sort}, goods_place=#{goods_place}, goods_title=#{goods_title}, goods_publisher=#{goods_publisher}, goods_price=#{goods_price}, goods_sales_price=#{goods_sales_price}, goods_point=#{goods_point}, goods_expired_date=#{goods_expired_date}, goods_status=#{goods_status}
where goods_id=#{goods_id}

update TICKET_ORDER
delivery_state=#{delivery_state}, delivery_address=#{delivery_address},
where order_id=#{order_id}
